import React from 'react'

export default class App extends React.Component {
  render() {
    console.log('frontend start')
    return <div className='wrapper'>
      <ToDo />
    </div>
  }
}

class ToDo extends React.Component {
  constructor() {
    super()
    this.state = {
      todos: [
        {id: '1', text: 'Купить хлеб'},
        {id: '1', text: 'Купить молоко'},
        {id: '1', text: 'Купить кофе'}
      ]
    }
  }

  render() {
    return (
      <div className='todo'>
        <AddToDo />  
        <ListToDo todos={this.state.todos}/>
      </div>
    )
  }
}

class AddToDo extends React.Component {
  render() {
    return (
      <form className='todo__add'>
        <input className='add__input'/>
        <button className='add__button'>
          Добавить  
        </button>
      </form>
    )
  }
}

class ListToDo extends React.Component {
  render() {
    const todos = this.props.todos.map( (value, index) => {
      <li className='list__item' key={index}>`${index}.va`</li>
    })

    return (
      <ul className='todo__list'>
        <li className='list__item'>{this.props.todos[0].text}</li>
        <li className='list__item'>{this.props.todos[1].text}</li>
        <li className='list__item'>{this.props.todos[2].text}</li>
      </ul>
    )
  }
}
