import React from 'react'

export default class App extends React.Component {



  render() {
    return <div className='wrapper'>
      {console.log(idmin)}
      <ToDo />
    </div>
  }
}

class ToDo extends React.Component {
  constructor() {
    super()
    this.state = {
      todos: [
        {id: '1', text: 'Купить хлеб'},
        {id: '2', text: 'Купить молоко'},
        {id: '3', text: 'Купить кофе'}
      ]
    }

    this.addTodos = this.addTodos.bind(this)
  }

  addTodos(newTodoText) {
    // this.state.todos.unshift({id: '1', text: newTodoText})
    // console.log(this.state.todos)
    // this.setState({todos: this.state.todos})

    this.setState( state => {
      const cloneTodos = Object.assign([], state.todos) //клонирование массива
      cloneTodos.unshift({id: '5', text: newTodoText})
      return {todos: cloneTodos}
    })
  }

  render() {
    return (
      <div className='todo'>
        <AddToDo addTodos={this.addTodos}/>  
        <ListToDo todos={this.state.todos}/>
      </div>
    )
  }
}

class AddToDo extends React.Component {
  constructor() {
    super()
    this.state = {
      addText: ''
    }

    this.handleChange = this.handleChange.bind(this)
    this.handleAdd = this.handleAdd.bind(this)
  }

  handleChange(evt) {
    this.setState({addText: evt.target.value})
  }

  handleAdd(evt) {
    evt.preventDefault()
    this.props.addTodos(this.state.addText)
    this.setState({addText: ''}) 
  }

  render() {
    return (
      <form className='todo__add'>
        <input className='add__input' value={this.state.addText} onChange={this.handleChange}/>
        <button className='add__button' onClick={this.handleAdd}>
          Добавить  
        </button>
      </form>
    )
  }
}

class ListToDo extends React.Component {
  render() {
    const todos = this.props.todos.map( (value, index) => {
      return <li className='list__item' key={index}>
        {`${index + 1}. ${value.text}`}
        <button className='btn__change'>
          Изменить
        </button>
        <button className='btn__delete'>
          Удалить
        </button>
      </li>
    })

    return (
      <ul className='todo__list'>
        {todos}
      </ul>
    )
  }
}
